import {Component} from '@angular/core';
import {CommonModule} from '@angular/common';
import {FormsModule} from '@angular/forms';

@Component({
  selector: 'app-hotel',
  imports: [CommonModule, FormsModule],
  templateUrl: './hotel.html',
  styleUrl: './hotel.css'
})
export class Hotel {
  hotels = [
    {
      name: 'Grand Palace Hotel',
      location: 'Downtown, City Center',
      rating: 4.5,
      price: 4500,
      image: 'data:image/jpeg;base64,',
      desc: 'Experience luxury and comfort in the heart of the city.',
      rooms: 20
    },
    {
      name: 'Ocean View Resort',
      location: 'Beachside',
      rating: 4.2,
      price: 3800,
      image: 'data:image/jpeg;base64,',
      desc: 'Relax by the ocean with stunning views.',
      rooms: 15
    },
    {
      name: 'Mountain Retreat',
      location: 'Hills',
      rating: 4.7,
      price: 5200,
      image: 'data:image/jpeg;base64,',
      desc: 'A peaceful getaway in the hills.',
      rooms: 10
    }
  ];

  newHotel = {
    name: '',
    location: '',
    rating: 0,
    price: 0,
    image: '',
    desc: '',
    rooms: 0
  };

  selectedHotel: any = null;
  editHotelData: any = {};
  editHotelIndex: number = -1;

  addHotel() {
    this.hotels.push({...this.newHotel});
    this.newHotel = {name: '', location: '', rating: 0, price: 0, image: '', desc: '', rooms: 0};
    const modal = document.getElementById('addHotelModal');
    if (modal) {
      (window as any).bootstrap.Modal.getInstance(modal).hide();
    }
  }

  showMore(index: number) {
    this.selectedHotel = this.hotels[index];
  }

  editHotel(index: number) {
    this.editHotelIndex = index;
    this.editHotelData = {...this.hotels[index]};
  }

  deleteHotel(index: number) {
    this.hotels.splice(index, 1);
  }

  updateHotel() {
    if (this.editHotelIndex > -1) {
      this.hotels[this.editHotelIndex] = {...this.editHotelData};
      this.editHotelIndex = -1;
      this.editHotelData = {};
      const modal = document.getElementById('editHotelModal');
      if (modal) {
        (window as any).bootstrap.Modal.getInstance(modal).hide();
      }
    }
  }
}
